import React, { Component } from 'react';
import './App.css';
import Options from './Options/Options';

class App extends Component {
  state={
    persons: [
      { name: "Coolboy55 ",
        inputContent: ''
      }
    ],
     counter: ''  
  }




  storeTxtArea = (event) => {
    console.log(event.target.value)
    let txtAreaCharacterCount = event.target.value.length
    console.log(txtAreaCharacterCount)
     return (txtAreaCharacterCount) => {
      this.setState({
        persons: [ { name: "Coolboy55 " }, { name: "Brrrrr " }, { name: "we just changed the state of this app when you click the button " }],
        counter: txtAreaCharacterCount
        // { counter: txtAreaCharacterCount }

      })

    }
    }

  switchNameHandler = () => {
    console.log('was clicked!');
    this.setState({
      persons: [
        { name: "Coolboy55 " },
      { name: "Brrrrr " },
      { name: "we just changed the state of this app when you click the button ", }
      ],

    })

  }
  render() {
    return (
      <div className="App">
        <p><button onClick={this.switchNameHandler}>switch Name Handler</button></p>


        <h1>Hi this is a React App</h1>
        <Options word=  {this.state.persons[0].name} price = "$20 and a box of pizza and moutian dew">Text between Options's element tags are called children</Options>
        <input className="input"  onChange={this.storeTxtArea}></input>  
        <div>{this.state.counter}</div>
      </div>
    );
  }
}

export default App;
