/*
1. Create an input field (in App component) with a change listener which outputs the length of the entered text 
below it (e.g. in a paragraph)

2. Create a new component (=> ValidationComponent) which receives the text length as a prop

3.Inside the ValidationComponent, either output "Text too short" or "Text long enough " depending on the text
length (e.g. take 5 as minimum length)

4. Create another component (=> CharComponent) and style it as an inline box (=>display: inline-block,
   padding: 16px, text-align: center, margin: 16px,border: 1px solid block).

5. Render a list of CharComponents where each CharComponent receives a different letter of the entered text (in
  the initial input field) as a prop.
6. When you click a CharComponent, it should be remvoed from the entered text.
*/

import React, { Component } from 'react';
import './App.css';
import Options from './Options/Options';

class App extends Component {
  state={
    persons: [
      { name: "Coolboy55 ",
        inputContent: ''
      }
    ],
     counter: ''  
  }

  storeTxtArea = (event) => {
    console.log(event.target.value)
    const txtAreaCharacterCount = event.target.value.length
    console.log(txtAreaCharacterCount)
      this.setState({
        persons: [ { name: "Coolboy55 " }, { name: "Brrrrr " }, { name: "we just changed the state of this app when you click the button " }],
        counter: txtAreaCharacterCount      
      })
  }

  switchNameHandler = () => {
    console.log(' switchNameHandler was clicked!');
    this.setState({
      persons: [
        { name: "we just changed the state of this app when you click the button  " }],
      })
    }

  render() {
    const {persons,counter,storeTxtArea} = this.state; // get in the habbit of doing this here 
    return (
      <div className="App">
        <p><button onClick={this.switchNameHandler}>switch Name Handler</button></p>


        <h1>Hi this is a React App</h1>
        <Options word= { persons[0].name } price = "$20 and a box of pizza and moutian dew">Text between Options's element tags are called children</Options>
        <textarea className="input"  onChange={this.storeTxtArea} placeholder="Insert your thoughts about anything in here"></textarea>  
        <div>{counter}</div>
      </div>
    );
  }
}

export default App;
